-- Update the existing products to have enhanced filter data
UPDATE products 
SET 
    is_flash_sale = (random() < 0.15),
    is_clearance = (random() < 0.12),
    is_trending = (random() < 0.20),
    is_new_this_week = (random() < 0.08),
    is_top_selling = (random() < 0.18),
    is_featured = (random() < 0.10),
    is_hot_deal = (random() < 0.07),
    is_dont_miss = (random() < 0.05),
    discount_percentage = CASE 
        WHEN random() < 0.3 THEN 10 + (random() * 30)::int
        ELSE 0
    END,
    rating_average = 3.0 + (random() * 2.0),
    rating_count = 25 + (random() * 500)::int,
    updated_at = now()
WHERE id IN ('prod-1', 'prod-2', 'prod-3', 'prod-4', 'prod-5', 'prod-6', 'prod-7', 'prod-8');

-- Update original_price for products with discounts
UPDATE products 
SET original_price = price * (1 + (discount_percentage / 100.0))
WHERE discount_percentage > 0 AND original_price IS NULL;